/* syntax highlight*/

figure.highlight,
.codeblock {
    background: #f7f8f8;
    margin: 10px 0;
    line-height: 1.1em;
    color: #333;
    padding-top: 15px;
    overflow: hidden;

    table {
        display: block;
        width: 100%;
    }

    // Reset for tag `pre` and  for class `.gutter`, `.code`, `.tag`
    pre,
    .gutter,
    .code,
    .tag {
        background-color: inherit;
        font-family: Menlo, Consolas, monospace;
        border: none;
        padding: 0;
        margin: 0;
        // To override cursor attribute of `.tag` components
        cursor: text;
    }

    // To align line number and line of code regardless if there is a scroll bar or not
    .gutter,
    .code {
        vertical-align: top;
    }

    // hide gutter when code is plain text
    &.plain {
        .gutter {
            display: none;
        }
    }

    // Meta bar which contains name of the file and his link
    figcaption {
        font-size: 13px;
        padding: 0 15px 20px;
        margin: 0;
        background: #f7f8f8;
        color: #999999;

        a {
            float: right;
            color: #01579f;
        }
    }

    // Gutter which contains line numbers
    .gutter {
        background: #f7f8f8;
        border-right: 1px solid #e6e6e6;
        padding: 0.3em 15px;
        user-select: none;
    }

    // Code container
    .code {
        padding: 0.3em 15px 0.3em 1em;
        width: 100%;

        pre {
            max-width: 700px;
            overflow-x: auto;
            overflow-y: hidden;
        }
    }

    // All lines in gutter and code container
    .line {
        height: 1.3em;
        font-size: 13px;
    }
}

// Gist
.gist {

    .line,
    .line-number {
        font-family: Menlo, Consolas, monospace;
        font-size: 1em;
        margin: 0 0 5px 0;
        user-select: none;
    }
}

// Highlight code coloration
.highlight {

    // General
    .comment {
        color: #969896;
    }

    .string {
        color: #183691;
    }

    .keyword {
        color: #a71d5d;
    }

    // ApacheConf
    &.apacheconf .code {

        .common,
        .nomarkup,
        .attribute,
        .variable,
        .cbracket,
        .keyword {
            color: #0086b3;
        }

        .sqbracket {
            color: #df5000;
        }

        .section,
        .tag {
            color: #63a35c;
        }
    }

    // Bash
    &.bash .code {
        .shebang {
            color: #969896;
        }

        .literal,
        .built_in {
            color: #0086b3;
        }

        .variable {
            color: #333;
        }

        .title {
            color: #795da3;
        }
    }

    // coffescript
    &.coffeescript .code {
        .title {
            color: #795da3;
        }

        .literal,
        .built_in,
        .number {
            color: #0086b3;
        }

        .reserved,
        .attribute {
            color: #1d3e81;
        }

        .subst,
        .regexp,
        .attribute {
            color: #df5000;
        }
    }

    // C/C++
    &.cpp .code,
    &.c .code {
        .preprocessor {
            color: #df5000;
        }

        .meta-keyword {
            color: #a71d5d;
        }

        .title {
            color: #795da3;
        }

        .number,
        .built_in {
            color: #0086b3;
        }
    }

    // Clojure
    &.clj .code {
        .builtin-name {
            color: #df5000;
        }

        .name {
            color: #795da3;
        }

        .number {
            color: #0086b3;
        }
    }

    // C#
    &.cs .code {

        .preprocessor,
        .preprocessor .keyword {
            color: #333;
        }

        .title {
            color: #795da3;
        }

        .number,
        .built_in {
            color: #0086b3;
        }

        .xmlDocTag,
        .doctag {
            color: #63a35c;
        }
    }

    // CSS
    &.css .code {

        .at_rule,
        .important,
        .meta {
            color: #a71d5d;
        }

        .attribute,
        .hexcolor,
        .number,
        .function {
            color: #0086b3;
        }

        .attr_selector,
        .value {
            color: #333;
        }

        .id,
        .class,
        .pseudo,
        .selector-pseudo {
            color: #795da3;
        }

        .tag,
        .selector-tag {
            color: #63a35c;
        }
    }

    // Diff
    &.diff .code {

        .chunk,
        .meta {
            color: #795da3;
            font-weight: bold;
        }

        .addition {
            color: #55a532;
            background-color: #eaffea;
        }

        .deletion {
            color: #bd2c00;
            background-color: #ffecec;
        }
    }

    // HTTP
    &.http .code {

        .attribute,
        .attr {
            color: #183691;
        }

        .literal {
            color: #0086b3;
        }

        .request {
            color: #a71d5d;
        }
    }

    // INI
    &.ini .code {

        .title,
        .section {
            color: #795da3;
        }

        .setting,
        .attr {
            color: #a71d5d;
        }

        .value,
        .keyword {
            color: #333;
        }
    }

    // JAVA
    &.java .code {
        .title {
            color: #795da3;
        }

        .javadoc {
            color: #969896;
        }

        .meta,
        .annotation,
        .javadoctag {
            color: #a71d5d;
        }

        .number {
            color: #0086b3;
        }

        .params {
            color: #1d3e81;
        }
    }

    // JavaScript
    &.js .code {

        .built_in,
        .title {
            color: #795da3;
        }

        .javadoc {
            color: #969896;
        }

        .tag,
        .javadoctag {
            color: #a71d5d;
        }

        .tag .title {
            color: #333;
        }

        .regexp {
            color: #df5000;
        }

        .literal,
        .number {
            color: #0086b3;
        }
    }

    // JSON
    &.json .code {
        .attribute {
            color: #183691;
        }

        .number,
        .literal {
            color: #0086b3;
        }
    }

    // Makefile
    &.mak .code {
        .constant {
            color: #333;
        }

        .title {
            color: #795da3;
        }

        .keyword,
        .meta-keyword {
            color: #0086b3;
        }
    }

    // Markdown
    &.md .code {

        .value,
        .link_label,
        .strong,
        .emphasis,
        .blockquote,
        .quote,
        .section {
            color: #183691;
        }

        .link_reference,
        .symbol,
        .code {
            color: #0086b3;
        }

        .link_url,
        .link {
            text-decoration: underline;
        }
    }

    // Nginx
    &.nginx .code {

        .title,
        .attribute {
            color: #a71d5d;
        }

        .built_in,
        .literal {
            color: #0086b3;
        }

        .regexp {
            color: #183691;
        }

        .variable {
            color: #333;
        }
    }

    // Objective-C
    &.objectivec .code {

        .preprocessor,
        .meta {
            color: #a71d5d;

            .title {
                color: #df5000;
            }
        }

        .meta-string {
            color: #183691;
        }

        .title {
            color: #795da3;
        }

        .literal,
        .number,
        .built_in {
            color: #0086b3;
        }
    }

    // Perl
    &.perl .code {
        .sub {
            color: #795da3;
        }

        .title {
            color: #795da3;
        }

        .regexp {
            color: #df5000;
        }
    }

    // PHP
    &.php .code {

        .phpdoc,
        .doctag {
            color: #a71d5d;
        }

        .regexp {
            color: #df5000;
        }

        .literal,
        .number {
            color: #0086b3;
        }

        .title {
            color: #795da3;
        }
    }

    // Python
    &.python .code {

        .decorator,
        .title,
        .meta {
            color: #795da3;
        }

        .number {
            color: #0086b3;
        }
    }

    // Ruby
    &.ruby .code {

        .parent,
        .title {
            color: #795da3;
        }

        .prompt,
        .constant,
        .number,
        .subst .keyword,
        .symbol {
            color: #0086b3;
        }
    }

    // SQL
    &.sql {
        .built_in {
            color: #a71d5d;
        }

        .number {
            color: #0086b3;
        }
    }

    // XML, HTML
    &.xml,
    &.html {
        .tag {
            color: #333;
        }

        .value {
            color: #183691;
        }

        .attribute,
        .attr {
            color: #795da3;
        }

        .title,
        .name {
            color: #63a35c;
        }
    }

    // Puppet
    &.puppet {
        .title {
            color: #795da3;
        }

        .function {
            color: #0086b3;
        }

        .name {
            color: #a71d5d;
        }

        .attr {
            color: #0086b3;
        }
    }

    // LESS
    &.less {

        .tag,
        .at_rule {
            color: #a71d5d;
        }

        .number,
        .hexcolor,
        .function,
        .attribute {
            color: #0086b3;
        }

        .built_in {
            color: #df5000;
        }

        .id,
        .pseudo,
        .class,
        .selector-id,
        .selector-class,
        .selector-tag {
            color: #795da3;
        }
    }

    // Lisp
    &.lisp .code {
        .name {
            color: #df5000;
        }

        .number {
            color: #0086b3;
        }
    }

    // SCSS
    &.scss {

        .tag,
        .at_rule,
        .important {
            color: #a71d5d;
        }

        .number,
        .hexcolor,
        .function,
        .attribute {
            color: #0086b3;
        }

        .variable {
            color: #333;
        }

        .built_in {
            color: #df5000;
        }

        .id,
        .pseudo,
        .class,
        .preprocessor,
        .selector-class,
        .selector-id {
            color: #795da3;
        }

        .tag,
        .selector-tag {
            color: #63a35c;
        }
    }

    // Stylus
    &.stylus {
        .at_rule {
            color: #a71d5d;
        }

        .tag,
        .selector-tag {
            color: #63a35c;
        }

        .number,
        .hexcolor,
        .attribute,
        .params {
            color: #0086b3;
        }

        .class,
        .id,
        .pseudo,
        .title,
        .selector-id,
        .selector-pseudo,
        .selector-class {
            color: #795da3;
        }
    }

    // Go
    &.go {
        .typename {
            color: #a71d5d;
        }

        .built_in,
        .constant {
            color: #0086b3;
        }
    }

    // Swift
    &.swift {
        .preprocessor {
            color: #a71d5d;
        }

        .title {
            color: #795da3;
        }

        .built_in,
        .number,
        .type {
            color: #0086b3;
        }
    }

    // YAML
    &.yml {

        .line,
        .attr {
            color: #63a35c;
        }

        .line,
        .string,
        .type,
        .literal,
        .meta {
            color: #183691;
        }

        .number {
            color: #0086b3;
        }
    }
}


/* post navigator*/

.post-nav {
    overflow: hidden;
    margin-top: 15px;
    margin-bottom: 20px;
    padding: 10px;
    white-space: nowrap;
    border-top: 1px solid #eee;

    a {
        display: inline-block;
        line-height: 25px;
        font-size: 15px;
        color: #555;
        border-bottom: none;
        float: left;

        &.pre {
            float: left;

            &:before {
                font-family: "FontAwesome";
                content: "\f0d9";
                padding-right: 0.3em;
            }
        }

        &.next {
            float: right;

            &:after {
                font-family: "FontAwesome";
                content: "\f0da";
                padding-left: 0.3em;
            }
        }

        &:hover {
            border-bottom: none;
            color: #222;
        }
    }
}


/* toc*/

.toc-article {
    border: 1px solid #bbb;
    border-radius: 7px;
    margin: 1.1em 0 0 2em;
    padding: 0.7em 0.7em 0 0.7em;
    max-width: 40%;
}

.toc-title {
    font-size: 120%;
}

#toc {
    line-height: 1em;
    float: right;

    .toc {
        padding: 0;
        margin: 0.5em;
        line-height: 1.8em;

        li {
            list-style-type: none;
        }
    }

    .toc-child {
        margin-left: 1em;
        padding-left: 0;
    }
}

@media print,
screen and (max-width: 48em) {
    .toc-article {
        margin: 0em;
        max-width: 100%;
    }

    .clear {
        padding: 2.1em 0em 0em 0em;
    }

    #toc {
        float: none;
    }
}


/* table*/

table {
    margin: auto auto 15px;
    width: 100%;
    background: transparent;
    border-collapse: collapse;
    border-spacing: 0;
    text-align: left;

    th {
        font-weight: bold;
        padding: 5px 10px;
        border-bottom: 2px solid #909ba2;
    }

    td {
        padding: 5px 10px;
    }

    tr {
        &:nth-child(2n) {
            background: #f7f8f8;
        }
    }
}


/* article-share*/

.article-share-link {
    cursor: pointer;
    float: right;
    margin-left: 20px;

    &:before {
        font-family: "FontAwesome";
        content: "\f064";
        padding-right: 6px;
    }
}

.article-share-box {
    position: absolute;
    display: none;
    background: #fff;
    box-shadow: 1px 2px 10px rgba(0, 0, 0, 0.2);
    border-radius: 3px;
    margin-left: -145px;
    overflow: hidden;
    z-index: 1;

    &.on {
        display: block;
    }
}

.article-share-input {
    width: 100%;
    background: none;
    box-sizing: border-box;
    font: 14px "Helvetica Neue", Helvetica, Arial, sans-serif;
    padding: 0 15px;
    color: #555;
    outline: none;
    border: 1px solid #ddd;
    border-radius: 3px 3px 0 0;
    height: 36px;
    line-height: 36px;
}

.article-share-links {
    clearfix: none;
    background: #eee;
}

.article-share-element {
    width: 50px;
    height: 36px;
    display: block;
    float: left;
    position: relative;
    color: #999;
    text-shadow: 0 1px #fff;

    &:before {
        font-size: 20px;
        font-family: "FontAwesome";
        width: 20px;
        height: 20px;
        position: absolute;
        top: 50%;
        left: 50%;
        margin-top: -10px;
        margin-left: -10px;
        text-align: center;
    }

    &:hover {
        color: #fff;
    }
}

.article-share-twitter {
    @extend .article-share-element;

    &:before {
        content: "\f099";
    }

    &:hover {
        background: #00aced;
    }
}

.article-share-facebook {
    @extend .article-share-element;

    &:before {
        content: "\f09a";
    }

    &:hover {
        background: #3b5998;
    }
}

.article-share-weibo {
    @extend .article-share-element;

    &:before {
        content: "\f18a";
    }

    &:hover {
        background: #d44137;
    }
}

.article-share-qrcode {
    @extend .article-share-element;

    &:before {
        content: "\f029";
    }

    &:hover,
    &:active {
        background: #38ad5a;

        &~.qrcode {
            display: block;
            text-align: center;
        }
    }
}

.qrcode {
    display: none;
}


/* search result*/

ul.search-result-list {
    padding-left: 10px;
}

a.search-result-title {
    font-weight: bold;
    font-size: 15px;
    color: #555;
}

p.search-result {
    color: #444;
    text-align: justify;
}

em.search-keyword {
    font-weight: bold;
    font-style: normal;
    color: #01579f;
}


/* Disqus Button */

.disqus_click_btn {
    line-height: 30px;
    margin: 0;
    min-width: 50px;
    padding: 0 14px;
    display: inline-block;
    font-family: "Roboto", "Helvetica", "Arial", sans-serif;
    font-size: 14px;
    font-weight: 400;
    letter-spacing: 0;
    overflow: hidden;
    will-change: box-shadow;
    transition: box-shadow .2s cubic-bezier(.4, 0, 1, 1), background-color .2s cubic-bezier(.4, 0, .2, 1), color .2s cubic-bezier(.4, 0, .2, 1);
    outline: 0;
    cursor: pointer;
    text-decoration: none;
    text-align: center;
    vertical-align: middle;
    border: 0;
    background: rgba(158, 158, 158, .2);
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
    color: #fff;
    background-color: #999999;
    text-shadow: 0
}


/* Footer Badge*/

.github-badge {
    display: inline-block;
    border-radius: 4px;
    text-shadow: none;
    font-size: 12px;
    color: #fff;
    line-height: 1.25;
    background-color: #ABBAC3;
    margin-bottom: 5px;

    a {
        color: #fff;
    }

    img {
        /*height: "calc(%s * 1.25)" % $fontsize-footnote*/
        height: 1.25em;
        vertical-align: top;
    }

    .badge-subject {
        display: inline-block;
        background-color: #555;
        padding: 4px 4px 4px 6px;
        border-top-left-radius: 4px;
        border-bottom-left-radius: 4px;
    }

    .badge-value {
        display: inline-block;
        padding: 4px 6px 4px 4px;
        border-top-right-radius: 4px;
        border-bottom-right-radius: 4px;
    }

    .bg-brightgreen {
        background-color: #4c1 !important;
    }

    .bg-green {
        background-color: #97ca00 !important;
    }

    .bg-yellowgreen {
        background-color: #a4a61d !important;
    }

    .bg-orange {
        background-color: #FE7D37 !important;
    }

    .bg-yellow {
        background-color: #dfb317 !important;
    }

    .bg-blueviolet {
        background-color: #8A2BE2 !important;
    }

    .bg-pink {
        background-color: #FFC0CB !important;
    }

    .bg-red {
        background-color: #e05d44 !important;
    }

    .bg-blue {
        background-color: #007EC6 !important;
    }

    .bg-grey,
    .bg-gray {
        background-color: #555 !important;
    }

    .bg-lightgrey,
    .bg-lightgray {
        background-color: #9f9f9f !important;
    }
}


/* Waline CSS*/

#waline {
    /* 字体大小 */
    --waline-font-size: 16px;

    /* 常规颜色 */
    --waline-white: #fff;
    --waline-light-grey: #999;
    --waline-dark-grey: #666;

    /* 主题色 */
    --waline-theme-color: #34495e;
    --waline-active-color: #bababa;

    /* 布局颜色 */
    --waline-text-color: #444;
    --waline-bgcolor: #fff;
    --waline-bgcolor-light: #f8f8f8;
    --waline-bgcolor-hover: #f0f0f0;
    --waline-border-color: #ddd;
    --waline-disable-bgcolor: #f8f8f8;
    --waline-disable-color: #bbb;
    --waline-code-bgcolor: #282c34;

    /* 特殊颜色 */
    --waline-bq-color: #f0f0f0;

    /* 头像 */
    --waline-avatar-size: 3.25rem;
    --waline-mobile-avatar-size: calc(var(--waline-avatar-size) * 9 / 13);

    /* 徽章 */
    --waline-badge-color: #34495e;
    --waline-badge-font-size: 0.775em;

    /* 信息 */
    --waline-info-bgcolor: #f8f8f8;
    --waline-info-color: #999;
    --waline-info-font-size: 0.625em;

    /* 渲染选择 */
    --waline-border: 1px solid var(--waline-border-color);
    --waline-avatar-radius: 50%;
    --waline-box-shadow: none;
}


/* Darkmode CSS*/

html[data-dark='true'] {
    background-color: #121212 !important;
    filter: invert(100%) hue-rotate(180deg) brightness(105%) contrast(85%);
    -webkit-filter: invert(100%) hue-rotate(180deg) brightness(105%) contrast(85%);

    img {
        filter: hue-rotate(180deg) contrast(100%) invert(100%);
        -webkit-filter: hue-rotate(180deg) contrast(100%) invert(100%);
    }
}

.darkmode-toggle {
    background: #a2a2a2;
    width: 3rem;
    height: 3rem;
    position: fixed;
    z-index: 999;
    border-radius: 50%;
    left: 32px;
    bottom: 32px;
    right: unset;
    cursor: pointer;
    transition: all 0.5s ease;
    display: flex;
    justify-content: center;
    align-items: center;
}


/* Links Layout*/

.link-items {
    display: flex;
    text-align: center;
    justify-content: left;
    flex-wrap: wrap;
    padding: 0px 0.5rem;
    list-style-type: disc;
    margin-block-start: 1em;
    margin-block-end: 1em;
    margin-inline-start: 0px;
    margin-inline-end: 0px;
    padding-inline-start: 20px;

    .link-item {
        display: inline-flex;
        --primary-color: #444;

        .link-url {
            display: inline-flex;
            text-align: center;
            justify-self: center;
            line-height: 1.5;
            width: 15rem;
            color: var(--primary-color, #000);
            padding: 0.5rem 1rem;
            margin: 0.5rem;
            transition: all 0.2s ease 0s;

            .link-left {
                display: inline-block;
                line-height: 0;

                .link-avatar {
                    width: 4rem;
                    height: 4rem;
                    border-radius: 50%;
                    transition: all 0.5s ease 0s;
                }
            }

            .link-info {
                padding-left: 0.6rem;
                display: block;

                .link-blog {
                    font-size: 1.24rem;
                    font-weight: 700;
                    margin: 0.42rem 0px;
                }

                .link-desc {
                    font-size: 0.8rem;
                    font-weight: normal;
                    margin-top: 0.5rem;
                    width: 10.5rem;
                    white-space: nowrap;
                    text-overflow: ellipsis;
                    overflow: hidden;
                }
            }
        }
    }
}


/* Recent-comments Waline Style*/

.waline-comment-content {
    margin: 6px;
    padding: 6px;
    border-radius: 6px;

    p {
        margin: 0;
        display: -webkit-box;
        overflow: hidden;
        -webkit-box-orient: vertical;
        -webkit-line-clamp: 3;
        /*这里设置第几行显示省略号，可以设置第一行或者其他行*/
    }
}

.waline-comment-content-author {
    text-align: end;
    margin-bottom: 6px;
}

/* article tags  */
.article-tag-list-item {
    font-size: 14px;
    line-height: 24px;
    display: inline-block;
    padding: 0 6px;
    border-radius: 3px;
    background: #f6f6f6;
    min-width: 30px;
    text-align: center;
    margin: 3px;
}

.article-tag-list-item a {
    text-align: center;
    color: #555;
}

.article-tag-list-item a::before {
    content: "\f02b";
    font-family: FontAwesome;
    margin-right: 4px;
}

.article-tag-list-item:hover {
    color: #555;
    background: #efefef;
}

.article-tag-list {
    padding-left: 0rem;
}

