            margin-top: 0;
            font-size: 16px;
            border-bottom: 1px solid #ddd;
            display: block;
            font-weight: normal;
        }

        .comments-title {
            color: #6E7173;
            line-height: 2.7;
            margin-top: 0;
            font-size: 16px;
            border-bottom: 0px solid #ddd;
            display: block;
            font-weight: normal;
        }

        .tagcloud {
            margin-top: 10px;

            a {
                line-height: 1.5;
                padding: 5px;
            }
        }

        ul {
            list-style: none;
            padding: 0;

            li {
                margin: 5px 0;
                line-height: 1.5;

                .wl-emoji {
                    width: 16px;
                }
            }
