  -moz-border-radius: 20px;
  border-radius: 20px;
  text-align: center;
  border: 4px solid #F5F5F6;
  line-height: 40px;
  font-size: 15px;
  float: left;
  position: absolute;
  top: 50%;
  margin-top: -20px;
  margin-left: -9px;
}

#process .timeline-centered .timeline-entry .timeline-entry-inner .timeline-icon.bg-primary {
  background-color: #303641;
  color: #fff;
}

#process .timeline-centered .timeline-entry .timeline-entry-inner .timeline-label {
  position: relative;
  background: #eee;
  padding: 30px;
  margin-left: 60px;
  -webkit-background-clip: padding-box;
  -moz-background-clip: padding;
  background-clip: padding-box;
  -webkit-border-radius: 3px;
  -moz-border-radius: 3px;
  border-radius: 3px;
}

#process .timeline-centered .timeline-entry .timeline-entry-inner .timeline-label::after {
  content: "";
  display: block;
  position: absolute;
  width: 0;
  height: 0;
  border-style: solid;
  border-width: 9px 9px 9px 0;
  border-color: transparent #eee transparent transparent;
  left: 0;
  top: 50%;
  margin-top: -9px;
  margin-left: -9px;
}

#process .line {
  position: absolute;
  display: block;
  width: 4px;
  background: #eee;
  top: -18px;
  right: -30px;
  bottom: -8px;
}

#process .present,
#process .born {
  font-size: 14px;
  font-family: "Georgia", serif, Helvetica, "Helvetica Neue", "Hiragino Sans GB", "Microsoft YaHei", "Source Han Sans CN", "WenQuanYi Micro Hei", Arial, sans-serif;
  font-style: italic;
  color: #333;
  padding: 10px;
  background-color: #eee;
  -webkit-border-radius: 3px;
  -moz-border-radius: 3px;
  border-radius: 3px;
}

#process .present::after,
#process .born::after {
  left: 100%;
  top: 50%;
  border: solid transparent;
  content: " ";
  height: 0;
  width: 0;
  position: absolute;
  border-color: rgba(136, 183, 213, 0);
  border-left-color: #eee;
  border-width: 10px;
  margin-top: -10px;
}

#process .present {
  position: absolute;
  top: -18px;
  right: 0;
  margin-top: -20px;
  line-height: 100%;
}

#process .born {
  position: absolute;
  bottom: -8px;
  right: 0;
  margin-bottom: -20px;
  line-height: 100%;
}

#process .dot_tp {
  position: absolute;
  top: -18px;
  right: -35px;
  background-color: transparent;
  height: 15px;
  width: 15px;
  margin-top: -13px;
  -webkit-border-radius: 50%;
  -moz-border-radius: 50%;
  border-radius: 50%;
  border: 3px solid #eee;
}

#process .dot_bt {
  position: absolute;
  bottom: -8px;
  right: -35px;
  background-color: transparent;
  height: 15px;
  width: 15px;
  margin-bottom: -13px;
  -webkit-border-radius: 50%;
  -moz-border-radius: 50%;
  border-radius: 50%;
  border: 3px solid #eee;
}

@media (max-width: 768px) {
  #process .line {
    right: 10px;
  }
  #process .timeline-centered .timeline-entry .timeline-entry-inner .timeline-label {
    margin-right: 30px;
    margin-left: 45px;
    padding: 20px;
  }
  #process .timeline-centered .timeline-entry {
    margin-right: 20px;
  }
  #process .dot_tp,
  #process .dot_bt {
    right: 5px;
  }
  #process .present,
  #process .born {
    right: 35px;
  }
}
