/* syntax highlight*/
figure.highlight,
.codeblock {
  background: #f7f8f8;
  margin: 10px 0;
  line-height: 1.1em;
  color: #333;
  padding-top: 15px;
  overflow: hidden;
}
figure.highlight table,
.codeblock table {
  display: block;
  width: 100%;
}
figure.highlight pre,
figure.highlight .gutter,
figure.highlight .code,
figure.highlight .tag,
.codeblock pre,
.codeblock .gutter,
.codeblock .code,
.codeblock .tag {
  background-color: inherit;
  font-family: Menlo, Consolas, monospace;
  border: none;
  padding: 0;
  margin: 0;
  cursor: text;
}
figure.highlight .gutter,
figure.highlight .code,
.codeblock .gutter,
.codeblock .code {
  vertical-align: top;
}
figure.highlight.plain .gutter,
.codeblock.plain .gutter {
  display: none;
}
figure.highlight figcaption,
.codeblock figcaption {
  font-size: 13px;
  padding: 0 15px 20px;
  margin: 0;
  background: #f7f8f8;
  color: #999999;
}
figure.highlight figcaption a,
.codeblock figcaption a {
  float: right;
  color: #01579f;
}
figure.highlight .gutter,
.codeblock .gutter {
  background: #f7f8f8;
  border-right: 1px solid #e6e6e6;
  padding: 0.3em 15px;
  user-select: none;
}
figure.highlight .code,
.codeblock .code {
  padding: 0.3em 15px 0.3em 1em;
  width: 100%;
}
figure.highlight .code pre,
.codeblock .code pre {
  max-width: 700px;
  overflow-x: auto;
  overflow-y: hidden;
}
figure.highlight .line,
.codeblock .line {
  height: 1.3em;
  font-size: 13px;
}

.gist .line,
.gist .line-number {
  font-family: Menlo, Consolas, monospace;
  font-size: 1em;
  margin: 0 0 5px 0;
  user-select: none;
}

