/* post navigator*/
.post-nav {
  overflow: hidden;
  margin-top: 15px;
  margin-bottom: 20px;
  padding: 10px;
  white-space: nowrap;
  border-top: 1px solid #eee;
}
.post-nav a {
  display: inline-block;
  line-height: 25px;
  font-size: 15px;
  color: #555;
  border-bottom: none;
  float: left;
}
.post-nav a.pre {
  float: left;
}
.post-nav a.pre:before {
  font-family: "FontAwesome";
  content: "\f0d9";
  padding-right: 0.3em;
}
.post-nav a.next {
  float: right;
}
.post-nav a.next:after {
  font-family: "FontAwesome";
  content: "\f0da";
  padding-left: 0.3em;
}
.post-nav a:hover {
  border-bottom: none;
  color: #222;
}

/* toc*/
.toc-article {
  border: 1px solid #bbb;
  border-radius: 7px;
  margin: 1.1em 0 0 2em;
  padding: 0.7em 0.7em 0 0.7em;
  max-width: 40%;
}

.toc-title {
  font-size: 120%;
}

#toc {
  line-height: 1em;
  float: right;
}
#toc .toc {
  padding: 0;
  margin: 0.5em;
  line-height: 1.8em;
}
#toc .toc li {
  list-style-type: none;
}
#toc .toc-child {
  margin-left: 1em;
  padding-left: 0;
}

@media print, screen and (max-width: 48em) {
  .toc-article {
    margin: 0em;
    max-width: 100%;
  }
  .clear {
    padding: 2.1em 0em 0em 0em;
  }
  #toc {
    float: none;
  }
}
/* table*/
table {
  margin: auto auto 15px;
  width: 100%;
  background: transparent;
  border-collapse: collapse;
  border-spacing: 0;
  text-align: left;
}
table th {
  font-weight: bold;
  padding: 5px 10px;
  border-bottom: 2px solid #909ba2;
}
table td {
  padding: 5px 10px;
}
table tr:nth-child(2n) {
  background: #f7f8f8;
}

/* article-share*/
.article-share-link {
  cursor: pointer;
  float: right;
  margin-left: 20px;
}
.article-share-link:before {
  font-family: "FontAwesome";
  content: "\f064";
  padding-right: 6px;
}

.article-share-box {
  position: absolute;
  display: none;
  background: #fff;
  box-shadow: 1px 2px 10px rgba(0, 0, 0, 0.2);
  border-radius: 3px;
  margin-left: -145px;
  overflow: hidden;
  z-index: 1;
}
.article-share-box.on {
  display: block;
}

.article-share-input {
  width: 100%;
  background: none;
  box-sizing: border-box;
  font: 14px "Helvetica Neue", Helvetica, Arial, sans-serif;
  padding: 0 15px;
  color: #555;
  outline: none;
  border: 1px solid #ddd;
  border-radius: 3px 3px 0 0;
  height: 36px;
  line-height: 36px;
}

.article-share-links {
  clearfix: none;
  background: #eee;
}

.article-share-element, .article-share-qrcode, .article-share-weibo, .article-share-facebook, .article-share-twitter {
  width: 50px;
  height: 36px;
  display: block;
  float: left;
  position: relative;
  color: #999;
  text-shadow: 0 1px #fff;
}
.article-share-element:before, .article-share-qrcode:before, .article-share-weibo:before, .article-share-facebook:before, .article-share-twitter:before {
  font-size: 20px;
  font-family: "FontAwesome";
  width: 20px;
  height: 20px;
  position: absolute;
  top: 50%;
  left: 50%;
  margin-top: -10px;
  margin-left: -10px;
  text-align: center;
}
.article-share-element:hover, .article-share-qrcode:hover, .article-share-weibo:hover, .article-share-facebook:hover, .article-share-twitter:hover {
  color: #fff;
}

.article-share-twitter:before {
  content: "\f099";
}
.article-share-twitter:hover {
  background: #00aced;
}

.article-share-facebook:before {
  content: "\f09a";
}
.article-share-facebook:hover {
  background: #3b5998;
}

.article-share-weibo:before {
  content: "\f18a";
}
.article-share-weibo:hover {
  background: #d44137;
}

.article-share-qrcode:before {
  content: "\f029";
}
.article-share-qrcode:hover, .article-share-qrcode:active {
  background: #38ad5a;
}
.article-share-qrcode:hover ~ .qrcode, .article-share-qrcode:active ~ .qrcode {
  display: block;
  text-align: center;
}

.qrcode {
  display: none;
}

/* search result*/
ul.search-result-list {
  padding-left: 10px;
}

a.search-result-title {
  font-weight: bold;
  font-size: 15px;
  color: #555;
}

p.search-result {
  color: #444;
  text-align: justify;
}

em.search-keyword {
  font-weight: bold;
  font-style: normal;
  color: #01579f;
}

/* Disqus Button */
.disqus_click_btn {
  line-height: 30px;
  margin: 0;
  min-width: 50px;
  padding: 0 14px;
  display: inline-block;
  font-family: "Roboto", "Helvetica", "Arial", sans-serif;
  font-size: 14px;
  font-weight: 400;
  letter-spacing: 0;
  overflow: hidden;
  will-change: box-shadow;
  transition: box-shadow 0.2s cubic-bezier(0.4, 0, 1, 1), background-color 0.2s cubic-bezier(0.4, 0, 0.2, 1), color 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  outline: 0;
  cursor: pointer;
  text-decoration: none;
  text-align: center;
  vertical-align: middle;
  border: 0;
  background: rgba(158, 158, 158, 0.2);
  box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 3px 1px -2px rgba(0, 0, 0, 0.2), 0 1px 5px 0 rgba(0, 0, 0, 0.12);
  color: #fff;
  background-color: #999999;
  text-shadow: 0;
}

/* Footer Badge*/
.github-badge {
  display: inline-block;
  border-radius: 4px;
  text-shadow: none;
  font-size: 12px;
  color: #fff;
  line-height: 1.25;
  background-color: #ABBAC3;
  margin-bottom: 5px;
}
.github-badge a {
  color: #fff;
}
.github-badge img {
  /*height: "calc(%s * 1.25)" % $fontsize-footnote*/
  height: 1.25em;
  vertical-align: top;
}
.github-badge .badge-subject {
  display: inline-block;
  background-color: #555;
  padding: 4px 4px 4px 6px;
  border-top-left-radius: 4px;
  border-bottom-left-radius: 4px;
}
.github-badge .badge-value {
  display: inline-block;
  padding: 4px 6px 4px 4px;
  border-top-right-radius: 4px;
  border-bottom-right-radius: 4px;
}
.github-badge .bg-brightgreen {
  background-color: #4c1 !important;
}
.github-badge .bg-green {
  background-color: #97ca00 !important;
}
.github-badge .bg-yellowgreen {
  background-color: #a4a61d !important;
}
.github-badge .bg-orange {
  background-color: #FE7D37 !important;
}
.github-badge .bg-yellow {
  background-color: #dfb317 !important;
}
.github-badge .bg-blueviolet {
  background-color: #8A2BE2 !important;
}
.github-badge .bg-pink {
  background-color: #FFC0CB !important;
}
.github-badge .bg-red {
  background-color: #e05d44 !important;
}
.github-badge .bg-blue {
  background-color: #007EC6 !important;
}
.github-badge .bg-grey,
.github-badge .bg-gray {
  background-color: #555 !important;
}
.github-badge .bg-lightgrey,
.github-badge .bg-lightgray {
  background-color: #9f9f9f !important;
}

/* Waline CSS*/
#waline {
  /* 字体大小 */
  --waline-font-size: 16px;
  /* 常规颜色 */
  --waline-white: #fff;
  --waline-light-grey: #999;
  --waline-dark-grey: #666;
  /* 主题色 */
  --waline-theme-color: #34495e;
  --waline-active-color: #bababa;
  /* 布局颜色 */
  --waline-text-color: #444;
  --waline-bgcolor: #fff;
  --waline-bgcolor-light: #f8f8f8;
  --waline-bgcolor-hover: #f0f0f0;
  --waline-border-color: #ddd;
  --waline-disable-bgcolor: #f8f8f8;
  --waline-disable-color: #bbb;
  --waline-code-bgcolor: #282c34;
  /* 特殊颜色 */
  --waline-bq-color: #f0f0f0;
  /* 头像 */
  --waline-avatar-size: 3.25rem;
  --waline-mobile-avatar-size: calc(var(--waline-avatar-size) * 9 / 13);
  /* 徽章 */
  --waline-badge-color: #34495e;
  --waline-badge-font-size: 0.775em;
  /* 信息 */
  --waline-info-bgcolor: #f8f8f8;
  --waline-info-color: #999;
  --waline-info-font-size: 0.625em;
  /* 渲染选择 */
  --waline-border: 1px solid var(--waline-border-color);
  --waline-avatar-radius: 50%;
  --waline-box-shadow: none;
}

/* Darkmode CSS*/
html[data-dark=true] {
  background-color: #121212 !important;
  filter: invert(100%) hue-rotate(180deg) brightness(105%) contrast(85%);
  -webkit-filter: invert(100%) hue-rotate(180deg) brightness(105%) contrast(85%);
}
html[data-dark=true] img {
  filter: hue-rotate(180deg) contrast(100%) invert(100%);
  -webkit-filter: hue-rotate(180deg) contrast(100%) invert(100%);
}

.darkmode-toggle {
  background: #a2a2a2;
  width: 3rem;
  height: 3rem;
  position: fixed;
  z-index: 999;
  border-radius: 50%;
  left: 32px;
  bottom: 32px;
  right: unset;
  cursor: pointer;
  transition: all 0.5s ease;
  display: flex;
  justify-content: center;
  align-items: center;
}

/* Links Layout*/
.link-items {
  display: flex;
  text-align: center;
  justify-content: left;
  flex-wrap: wrap;
  padding: 0px 0.5rem;
  list-style-type: disc;
  margin-block-start: 1em;
  margin-block-end: 1em;
  margin-inline-start: 0px;
  margin-inline-end: 0px;
  padding-inline-start: 20px;
}
.link-items .link-item {
  display: inline-flex;
  --primary-color: #444;
}
.link-items .link-item .link-url {
  display: inline-flex;
  text-align: center;
  justify-self: center;
  line-height: 1.5;
  width: 15rem;
  color: var(--primary-color, #000);
  padding: 0.5rem 1rem;
  margin: 0.5rem;
  transition: all 0.2s ease 0s;
}
.link-items .link-item .link-url .link-left {
  display: inline-block;
  line-height: 0;
}
.link-items .link-item .link-url .link-left .link-avatar {
  width: 4rem;
  height: 4rem;
  border-radius: 50%;
  transition: all 0.5s ease 0s;
}
.link-items .link-item .link-url .link-info {
  padding-left: 0.6rem;
  display: block;
}
.link-items .link-item .link-url .link-info .link-blog {
  font-size: 1.24rem;
  font-weight: 700;
  margin: 0.42rem 0px;
}
.link-items .link-item .link-url .link-info .link-desc {
  font-size: 0.8rem;
  font-weight: normal;
  margin-top: 0.5rem;
  width: 10.5rem;
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;
}

/* Recent-comments Waline Style*/
.waline-comment-content {
  margin: 6px;
  padding: 6px;
  border-radius: 6px;
}
.waline-comment-content p {
  margin: 0;
  display: -webkit-box;
  overflow: hidden;
  -webkit-box-orient: vertical;
  -webkit-line-clamp: 3;
  /*这里设置第几行显示省略号，可以设置第一行或者其他行*/
}

.waline-comment-content-author {
  text-align: end;
  margin-bottom: 6px;
}

/* article tags  */
.article-tag-list-item {
  font-size: 14px;
  line-height: 24px;
  display: inline-block;
  padding: 0 6px;
  border-radius: 3px;
  background: #f6f6f6;
  min-width: 30px;
  text-align: center;
  margin: 3px;
}

.article-tag-list-item a {
  text-align: center;
  color: #555;
}

.article-tag-list-item a::before {
  content: "\f02b";
  font-family: FontAwesome;
  margin-right: 4px;
}

.article-tag-list-item:hover {
  color: #555;
  background: #efefef;
}

.article-tag-list {
  padding-left: 0rem;
}

Page._content:
+function($) {
    'use strict';

    // Resize code blocks to fit the screen width

    var CodeBlockResizer = function(elem) {
        this.$codeBlocks = $(elem);
    };

    CodeBlockResizer.prototype = {
        /**
         * Run main feature
         */
        run: function() {
            var self = this;
            // resize all codeblocks
            self.resize();
            // resize codeblocks when window is resized
            $(window).smartresize(function() {
                self.resize();
            });
        },

        /**
         * Resize codeblocks
         */
        resize: function() {
            var self = this;
            self.$codeBlocks.each(function() {
                var $gutter = $(this).find('.gutter');
                var $code = $(this).find('.code');
                // get padding of code div
                var codePaddings = $code.width() - $code.innerWidth();
                // code block div width with padding - gutter div with padding + code div padding
                var width = $(this).outerWidth() - $gutter.outerWidth() + codePaddings;
                // apply new width
                $code.css('width', width);
                $code.children('pre').css('width', width);
            });
        }
    };

    $(document).ready(function() {
        // register jQuery function to check if an element has an horizontal scroll bar
        $.fn.hasHorizontalScrollBar = function() {
            return this.get(0).scrollWidth > this.innerWidth();
        };
        var resizer = new CodeBlockResizer('figure.highlight');
        resizer.run();
    });
}(jQuery);

